CREATE TABLE `xmb_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `orderid` varchar(50) DEFAULT NULL COMMENT '订单号',
  `gid` varchar(255) DEFAULT NULL COMMENT '商品id',
  `amount` varchar(255) DEFAULT NULL COMMENT '商品数量',
  `price` varchar(255) DEFAULT NULL COMMENT '商品单价',
  `totalprice` float DEFAULT NULL COMMENT '商品总价',
  `nid` varchar(255) DEFAULT NULL COMMENT '商品规格',
  `val` varchar(255) DEFAULT NULL COMMENT '商品规格参数',
  `aid` int(11) DEFAULT NULL COMMENT '会员id',
  `addressman` varchar(255) DEFAULT NULL COMMENT '收货人姓名',
  `addressmobile` varchar(255) DEFAULT NULL COMMENT '收货人手机号',
  `status` int(11) DEFAULT '1' COMMENT '订单状态，1，未付款，2，已付款未发货，3已发货，4已签收未评价，5已评价，-1取消订单，-2退货，-3换货',
  `paytime` int(11) DEFAULT NULL COMMENT '付款时间',
  `fhtime` int(11) DEFAULT NULL COMMENT '发货时间',
  `thtime` int(11) DEFAULT NULL COMMENT '退换货时间',
  `exp` varchar(100) DEFAULT NULL COMMENT '物流公司名称',
  `expnum` varchar(255) DEFAULT NULL COMMENT '物流单号',
  `thexp` varchar(100) DEFAULT NULL COMMENT '退换货物流公司名称',
  `thexpnum` varchar(255) DEFAULT NULL COMMENT '退换货物流单号',
  `type` int(11) DEFAULT '1' COMMENT '类型，1为第三方支付，2余额支付',
  `fhbeizhu` varchar(255) DEFAULT NULL COMMENT '发货备注',
  `thbeizhu` varchar(255) DEFAULT NULL COMMENT '退换货备注',
  `addtime` int(11) DEFAULT NULL COMMENT '订单生成时间',
  `paytype` int(11) DEFAULT NULL COMMENT '支付方式，1支付宝，2微信，3银联',
  `address` varchar(255) DEFAULT NULL COMMENT '收货地址',
  `paybeizhu` varchar(255) DEFAULT NULL COMMENT '付款备注',
  `addressid` int(11) DEFAULT NULL COMMENT '收货地址id',
  `coupon` varchar(255) DEFAULT NULL COMMENT '优惠券使用情况',
  `is_cart` int(11) DEFAULT '-1' COMMENT '是否来自购物车，1是，-1否',
  `shtime` int(11) DEFAULT NULL COMMENT '确认收货时间',
  `pjtime` int(11) DEFAULT NULL COMMENT '添加评价时间',
  `unusual` varchar(100) DEFAULT NULL COMMENT '记录订单异常，1正常，2，退货，3换货',
  `pingjia` varchar(100) DEFAULT NULL COMMENT '订单评价，1正常，2已评',
  `exppy` varchar(120) DEFAULT NULL COMMENT '快递公司拼音',
  `score` float DEFAULT '0' COMMENT '使用积分数量',
  `remain` float DEFAULT '0' COMMENT '余额支付金额',
  `online` float DEFAULT '0' COMMENT '第三方支付金额',
  `cpid` int(11) DEFAULT NULL COMMENT '会员优惠券id',
  `yunfei` float DEFAULT '0' COMMENT '运费',
  `discount` int(11) NOT NULL DEFAULT '0' COMMENT '会员折扣',
  `token_push` varchar(255) NOT NULL DEFAULT '' COMMENT '验证token',
  `is_fa` int(10) DEFAULT '0' COMMENT '是否有发票，0没有，1有',
  `message` text COMMENT '留言',
  `ordertype` int(11) DEFAULT '1' COMMENT '订单类型，1正常2积分兑换',
  `is_wan` int(11) DEFAULT '1' COMMENT '地址留言是否完善，1未，2已完善',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=201 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;
